package lengthsafe

import (
	. "github.com/onsi/ginkgo/v2"
	. "github.com/onsi/gomega"
)

var splitFileNameStr = ``
var splitFileNameDirStr = ``
var splitFileNameRemainderStr = `123456789`
var splitDirNameStr = ``
var splitDirNameDirStr = ``
var splitDirNameRemainderStr = `123456789/123456789/123456789`
var splitOnDirStr = ``
var splitOnDirDirStr = ``
var splitOnDirRemainderStr = `123456789/123456789`

var _ = Describe("LengthSafeFile", func() {
	Describe("limitPath()", func() {
		Context("when path is shorter than PathMax", func() {
			It("should return dir and remainder", func() {
				l := &LengthSafeFile{}

				path := "/asdf/ghjkl"

				dir, remainder := l.limitPath(path)

				Ω(dir).Should(Equal(path))
				Ω(remainder).Should(Equal(""))
			})
		})

		Context("when path[:PathMax] splits the final file name", func() {
			It("should return dir and remainder", func() {
				l := &LengthSafeFile{}

				path := splitFileNameStr

				dir, remainder := l.limitPath(path)

				Ω(dir).Should(Equal(splitFileNameDirStr))
				Ω(remainder).Should(Equal(splitFileNameRemainderStr))
			})
		})

		Context("when path[:PathMax] splits a dir name", func() {
			It("should return dir and remainder", func() {
				l := &LengthSafeFile{}

				path := splitDirNameStr

				dir, remainder := l.limitPath(path)

				Ω(dir).Should(Equal(splitDirNameDirStr))
				Ω(remainder).Should(Equal(splitDirNameRemainderStr))
			})
		})

		Context("when path[:PathMax] end on '/'", func() {
			It("should return dir and remainder", func() {
				l := &LengthSafeFile{}

				path := splitOnDirStr

				dir, remainder := l.limitPath(path)

				Ω(dir).Should(Equal(splitOnDirDirStr))
				Ω(remainder).Should(Equal(splitOnDirRemainderStr))
			})
		})
	})

})
